const App = () => {
  let [numero, setNumero] = useState(0);
  function handleClick() {
    numero++

    setNumero(numero)
  }

  return (
    <div>
      <p>{numero}</p>
      <button onClick={handleClick}>clicar</button>
    </div>
  );
};

export default App;
