import React, { useState } from 'react';
import ButtonModal from './ButtonModal';

const App = () => {
  let [modal, setModal] = useState(false)

  return <div>
    <p>{modal ? 'Modal Aberto':'Modal Fechado'}</p>

    <ButtonModal setModal={setModal} />
  </div>;
};

export default App;
