import React, {useState} from "react";




function App() {
  const [ativacao, setAtivacao] = useState(true);
  let ativo = true;

  const handleClik = () => {
    setAtivacao(!ativacao);
  }

  return (
   <section>
      <button onClick={handleClik} disabled={!ativacao}>{ativacao ? 'Ativo':'Inativo'}</button>
   </section>
  );
}

export default App;
