import React, { useState } from 'react';

function App() {
  const [ativacao, setAtivacao] = useState(true);
  let ativo = true;
  let i = 0;
  const handleClik = () => {
    setAtivacao(!ativacao);

  };

  return (
    <section>
      <p>{`clicou ${i++}`}</p>
      <button onClick={handleClik} disabled={!ativacao}>
        {ativacao ? 'Ativo' : 'Inativo'}
      </button>
    </section>
  );
}

export default App;
